mapper.select:
SELECT 
FROM t_blog_article blog_article_
WHERE blog_article_.id = #{id,jdbcType=BIGINT}

mapper.update:
UPDATE t_blog_article
author_id = #{authorId,jdbcType=BIGINT}, tag_id = #{tagId,jdbcType=BIGINT}, sort_id = #{sortId,jdbcType=BIGINT}, status = #{status,jdbcType=INTEGER}, title = #{title,jdbcType=VARCHAR}, description = #{description,jdbcType=VARCHAR}, content = #{content,jdbcType=VARCHAR}, publish_date = #{publishDate,jdbcType=TIMESTAMP}, create_date = #{createDate,jdbcType=TIMESTAMP}, update_date = #{updateDate,jdbcType=TIMESTAMP},
WHERE id = #{id,jdbcType=BIGINT}
